mapper.update:

UPDATE tc_risk_trhld_manage
SET

trhld_sid = #{trhld_sid}
,target_id = #{target_id}
,target_type_cd = #{target_type_cd}
,risk_type_id = #{risk_type_id}
,risk_step_cd = #{risk_step_cd}
,risk_step_lvl = #{risk_step_lvl}
,trhld_ord = #{trhld_ord}
,start_risk_num = #{start_risk_num}
,close_risk_num = #{close_risk_num}
 ,update_date = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
,update_id = #{update_id}

WHERE trhld_sid = #{trhld_sid}

mapper.select:
SELECT a.facty_id, a.facty_nm, a.risk_type_nm, a.risk_type_id, CONCAT (MAX(dest_min_risk),'이상 ~ ',MAX(dest_max_risk),'이하') DGER, CONCAT (MAX(care_min_risk),'이상 ~ ',MAX(care_max_risk),'이하') CARE, CONCAT (MAX(warg_min_risk),'이상 ~ ',MAX(warg_max_risk),'이하') WARG, CONCAT (MAX(norl_min_risk),'이상 ~ ',MAX(norl_max_risk),'이하') NORL FROM( SELECT a.target_id facty_id,a.risk_type_id, (SELECT t.FACTY_NM from tb_facility t WHERE t.FACTY_ID = a.target_id) facty_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b WHERE a.target_type_cd='TT0001'
AND a.risk_type_id= #{risk_type_id}
and b.cd_grp_id='risk_step_cd' and a.risk_step_cd = b.cd_id group by a.target_id ,a.target_id, a.risk_type_id ) a GROUP BY a.facty_id, a.facty_nm, a.risk_type_nm, a.risk_type_id


	SELECT
		trhld_sid
		,target_id
		,target_type_cd
		,risk_type_id
		,risk_step_cd
		,trhld_ord
		,start_risk_num 
		,close_risk_num
		,risk_step_lvl
		,regist_date
		,regist_id
		,update_date
		,update_id
		,(SELECT t.trhld_ptn_cd FROM tc_risk_type t WHERE t.risk_type_id=a.risk_type_id) risk_type_nm 	
		,(SELECT t.cd_nm FROM tc_common_code t WHERE t.cd_grp_id='RISK_STEP_CD' AND t.cd_id = a.risk_step_cd) risk_step_nm
	FROM tc_risk_trhld_manage a
	WHERE 1=1

AND trhld_sid = #{trhld_sid}
AND target_id = #{target_id}
AND target_type_cd = #{target_type_cd}
AND risk_type_id = #{risk_type_id}
AND risk_step_cd = #{risk_step_cd}
AND trhld_ord = #{trhld_ord}
AND start_risk_num = #{start_risk_num}
AND close_risk_num = #{close_risk_num}
AND regist_date = #{regist_date}
AND regist_id = #{regist_id}
AND update_date = #{update_date}
AND update_id = #{update_id}


SELECT * FROM ( 
	SELECT 
		@rownum:=@rownum+1 RO, 
		A.* 
	FROM 
	(
		SELECT
			trhld_sid
			,target_id
			,target_type_cd
			,risk_type_id
			,risk_step_cd
			,risk_step_lvl
			,trhld_ord
			,start_risk_num
			,close_risk_num
			,regist_date
			,regist_id
			,update_date
			,update_id
			,(SELECT t.trhld_ptn_cd FROM tc_risk_type t WHERE t.risk_type_id=a.risk_type_id) risk_type_nm 	
			,(SELECT t.cd_nm FROM tc_common_code t WHERE t.cd_grp_id='RISK_STEP_CD' AND t.cd_id = a.risk_step_cd) risk_step_nm
		FROM tc_risk_trhld_manage a
		WHERE 1 = 1 

		 AND trhld_sid = #{trhld_sid}
		 AND target_id = #{target_id}
		 AND target_type_cd = #{target_type_cd}
		 AND risk_type_id = #{risk_type_id}
		 AND risk_step_cd = #{risk_step_cd}
		 AND trhld_ord = #{trhld_ord}
		 AND start_risk_num = #{start_risk_num}
		 AND close_risk_num = #{close_risk_num}
		 AND regist_date = #{regist_date}
		 AND regist_id = #{regist_id}
		 AND update_date = #{update_date}
		 AND update_id = #{update_id}

		ORDER BY update_date DESC
	) A,
	(SELECT @rownum:=0) TMP
) A
LIMIT  ${startRow},${endRow}


	SELECT count(1)  
	FROM tc_risk_trhld_manage
	WHERE 1 = 1 

AND trhld_sid = #{trhld_sid}
AND target_id = #{target_id}
AND target_type_cd = #{target_type_cd}
AND risk_type_id = #{risk_type_id}
AND risk_step_cd = #{risk_step_cd}
AND trhld_ord = #{trhld_ord}
AND start_risk_num = #{start_risk_num}
AND close_risk_num = #{close_risk_num}
AND regist_date = #{regist_date}
AND regist_id = #{regist_id}
AND update_date = #{update_date}
AND update_id = #{update_id}

SELECT a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.risk_type_nm, a.risk_type_id, CONCAT (MAX(dest_min_risk),'이상 ~ ',MAX(dest_max_risk),'이하') DGER, CONCAT (MAX(care_min_risk),'이상 ~ ',MAX(care_max_risk),'이하') CARE, CONCAT (MAX(warg_min_risk),'이상 ~ ',MAX(warg_max_risk),'이하') WARG, CONCAT (MAX(norl_min_risk),'이상 ~ ',MAX(norl_max_risk),'이하') NORL FROM( SELECT a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b, vw_process c WHERE a.TARGET_TYPE_CD='TT0002' AND b.CD_GRP_ID='RISK_STEP_CD' AND a.RISK_STEP_CD = b.CD_ID AND a.target_id = c.procs_id
AND c.facty_id= #{facty_id} AND a.risk_type_id= #{risk_type_id}
GROUP BY a.target_id , a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm ) a GROUP BY a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.risk_type_nm, a.risk_type_id

SELECT count(1) FROM( SELECT a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b, vw_process c WHERE a.TARGET_TYPE_CD='TT0002' AND b.CD_GRP_ID='RISK_STEP_CD' AND a.RISK_STEP_CD = b.CD_ID AND a.target_id = c.procs_id
AND c.facty_id= #{facty_id} AND a.risk_type_id= #{risk_type_id}
GROUP BY a.target_id , a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm ) a

SELECT a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.eqmt_id, a.eqmt_nm, a.risk_type_nm, a.risk_type_id, CONCAT (MAX(dest_min_risk),'이상 ~ ',MAX(dest_max_risk),'이하') DGER, CONCAT (MAX(care_min_risk),'이상 ~ ',MAX(care_max_risk),'이하') CARE, CONCAT (MAX(warg_min_risk),'이상 ~ ',MAX(warg_max_risk),'이하') WARG, CONCAT (MAX(norl_min_risk),'이상 ~ ',MAX(norl_max_risk),'이하') NORL FROM( SELECT a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b, vw_equipment c WHERE a.TARGET_TYPE_CD='TT0003' AND b.CD_GRP_ID='RISK_STEP_CD' AND a.RISK_STEP_CD = b.CD_ID AND a.target_id = c.eqmt_id
AND c.facty_id= #{facty_id} AND c.procs_id= #{procs_id} AND a.risk_type_id= #{risk_type_id}
GROUP BY a.target_id , a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm ) a GROUP BY a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.risk_type_nm, a.risk_type_id, a.eqmt_id, a.eqmt_nm

SELECT count(1) FROM( SELECT a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b, vw_equipment c WHERE a.TARGET_TYPE_CD='TT0003' AND b.CD_GRP_ID='RISK_STEP_CD' AND a.RISK_STEP_CD = b.CD_ID AND a.target_id = c.eqmt_id
AND c.facty_id= #{facty_id} AND c.procs_id= #{procs_id} AND a.risk_type_id= #{risk_type_id}
GROUP BY a.target_id , a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm ) a

SELECT a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.eqmt_id, a.eqmt_nm, a.meeq_id, a.meeq_nm, a.risk_type_nm, a.risk_type_id, CONCAT (MAX(dest_min_risk),'이상 ~ ',MAX(dest_max_risk),'이하') DGER, CONCAT (MAX(care_min_risk),'이상 ~ ',MAX(care_max_risk),'이하') CARE, CONCAT (MAX(warg_min_risk),'이상 ~ ',MAX(warg_max_risk),'이하') WARG, CONCAT (MAX(norl_min_risk),'이상 ~ ',MAX(norl_max_risk),'이하') NORL FROM( SELECT a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm, d.MEEQ_ID, d.meeq_nm, (SELECT t.risk_type_nm from tc_risk_type t WHERE t.risk_type_id = a.risk_type_id) risk_type_nm, min(case when b.CD_ADD_ITM = 'DGER' then start_risk_num END) dest_min_risk, max(case when b.CD_ADD_ITM = 'DGER' then close_risk_num END) dest_max_risk, min(case when b.CD_ADD_ITM = 'CARE' then start_risk_num END) care_min_risk, max(case when b.CD_ADD_ITM = 'CARE' then close_risk_num END) care_max_risk, min(case when b.CD_ADD_ITM = 'WARG' then start_risk_num END) warg_min_risk, max(case when b.CD_ADD_ITM = 'WARG' then close_risk_num END) warg_max_risk, min(case when b.CD_ADD_ITM = 'NORL' then start_risk_num END) norl_min_risk, max(case when b.CD_ADD_ITM = 'NORL' then close_risk_num END) norl_max_risk FROM tc_risk_trhld_manage a , tc_common_code b, vw_equipment c , tb_eqmt_tag d WHERE a.TARGET_TYPE_CD='TT0004' AND b.CD_GRP_ID='RISK_STEP_CD' AND a.RISK_STEP_CD = b.CD_ID AND d.meeq_id = a.target_id AND c.eqmt_id = d.EQMT_ID
