GROUP BY a.target_id , a.risk_type_id, c.procs_id,c.procs_nm, c.facty_id,c.facty_nm,c.eqmt_id,c.eqmt_nm, d.meeq_id,d.meeq_nm ) a GROUP BY a.facty_id, a.facty_nm, a.procs_id, a.procs_nm, a.risk_type_nm, a.risk_type_id, a.eqmt_id, a.eqmt_nm, a.meeq_id, a.meeq_nm

